import "./cart-form.css";
import { useState } from "react";

function insertSpaces(str) {
  return str.replace(/\d{4}(?=.)/g, "$& ");
}

const CartForm = ({ number, setNumber }) => {
  function handleChange(event) {
    const newValue = insertSpaces(event.target.value.replace(/\s/g, ""));
    setNumber(newValue);
  }

  return (
    <div className="cart-from-container">
      <form>
        Cardholder Name
        <input
          className="cardholder-name"
          type="text"
          placeholder="e.g. Jane Appleseed"
        />
        Card Number
        <input
          className="card-number"
          type="text"
          placeholder="e.g. 1234 5678 9123 0000"
          value={number}
          onChange={handleChange}
          maxLength={19}
          minLength={19}
        />
        Exp. Date (MM/YY)
        <input
          className="card-date-mm-yy"
          type="text"
          placeholder="MM"
          maxLength={2}
        />
        <input
          className="card-date-mm-yy"
          type="text"
          placeholder="YY"
          maxLength={2}
        />
        <input
          className="card-cvc"
          type="text"
          placeholder="e.g.123"
          maxLength={3}
        />
      </form>
    </div>
  );
};

export default CartForm;
