import "./cart-form.css";
import { useState } from "react";

function insertSpaces(str) {
  return str.replace(/\d{4}(?=.)/g, "$& ");
}

const CartForm = ({ number, setNumber, setName }) => {
  function handleChange(event) {
    const newValue = insertSpaces(event.target.value.replace(/\s/g, ""));
    setNumber(newValue);
  }

  return (
    <div className="cart-from-container">
      <form className="form">
        Cardholder Name
        <input
          className="cardholder-info"
          type="text"
          placeholder="e.g. Jane Appleseed"
          onChange={(e) => setName(e.target.value)}
        />
        Card Number
        <input
          className="cardholder-info"
          type="text"
          placeholder="e.g. 1234 5678 9123 0000"
          value={number}
          onChange={handleChange}
          maxLength={19}
          minLength={19}
        />
        <div className="card-date-container">
          Exp. Date (MM/YY)
          <input
            className="card-date-mm-yy"
            type="text"
            placeholder="MM"
            maxLength={2}
          />
          <input
            className="card-date-mm-yy"
            type="text"
            placeholder="YY"
            maxLength={2}
          />
          CVC
          <input
            className="card-cvc"
            type="text"
            placeholder="e.g. 123"
            maxLength={3}
          />
        </div>
        <button className="confirm-button" type="button">
          Confirm
        </button>
      </form>
    </div>
  );
};

export default CartForm;
